package examen2Trimestre;

import javafx.application.Application;
import javafx.fxml.FXMLLoader;
import javafx.scene.Parent;
import javafx.scene.Scene;
import javafx.scene.image.Image;
import javafx.stage.Stage;

import java.sql.SQLException;

public class Main extends Application {

	@Override
	public void start(Stage primaryStage) throws Exception {

		FXMLLoader loader = new FXMLLoader(getClass().getResource("universidad.fxml"));
		Parent root = loader.load();

		Scene scene = new Scene(root, 800, 400);

		String cssFile = getClass().getResource("style.css").toExternalForm();

		scene.getStylesheets().add(cssFile);

		primaryStage.setTitle("Estudiantes de la Universidad");
		primaryStage.getIcons().add(new Image(
				"data:image/png;base64,"));
		primaryStage.setScene(scene);
		primaryStage.show();

		// Mueve la l√≥gica de la base de datos al controlador
		String dbURL = "jdbc:mysql://127.0.0.1:17770/universidad";
		String dbUser = "root";
		String dbPassword = "";

		try {
			Controller controller = loader.getController();
			controller.inicializar(dbURL, dbUser, dbPassword);
		} catch (SQLException e) {
			e.printStackTrace();
		}
	}

	public static void main(String[] args) {
		launch(args);
	}
}
